// src/Hero.js
import React from 'react';
import './Hero.css';
import { Box, Typography, Button, Container } from '@mui/material';
import styled from '@emotion/styled';

const HeroBox = styled(Box)`
  padding: 50px 0;
  background-color: #e0f7fa;
  text-align: center;
`;


const Hero = ({ onOpenModal }) => {
  return (
    <div className="hero">
      <div className="hero-content">
      <HeroBox>
    <Container>
      <Typography variant="h3" gutterBottom>
        Refer & Earn Program
      </Typography>
      <Typography variant="h6" gutterBottom>
        Invite your friends and earn rewards when they enroll in our programs.
      </Typography>
      <Button variant="contained" color="primary" onClick={onOpenModal}>
        Refer Now
      </Button>
    </Container>
  </HeroBox>
      </div>
      <div className="hero-image">
        <img src="https://storage.googleapis.com/accredian-assets/Frontend_Assests/Images/Accredian-react-site-images/logo/scholar.svg" alt="Hero" />
      </div>
    </div>
  );
};

export default Hero;
